<html lang="nl" xmlns="http://www.w3.org/1999/xhtml" xmlns:v="urn:schemas-microsoft-com:vml">
  <custom name="opencounter" type="tracking"/>
  <head>

  %%[
Var @Salutation,@NewSalutation
Set @Salutation = [Apttus_Config2__Order__c:Apttus_Config2__PrimaryContactId__r:Salutation]

If @Salutation == 'Mr.' then
Set @NewSalutation = 'heer'
elseif @Salutation == 'Ms.' then
Set @NewSalutation = 'mevrouw'
elseif @Salutation == 'Mrs.' then
Set @NewSalutation = 'mevrouw'
elseif @Salutation == 'Dr.' then
Set @NewSalutation = 'prof. dr.'
elseif @Salutation == 'Prof.' then
Set @NewSalutation = 'De weledelgeleerde heer/vrouwe '
endif
]%%

     %%[ 

   Var  @SalesforceObject1,@rowCount1,@CRMRow1,@Quantity1,@OrderId,@totalQuantity,@Product1,@ProductCode1,@Translation1

   Set @OrderId1 = [Apttus_Config2__Order__c:Id]

   Set @SalesforceObject1 = RetrieveSalesforceObjects('Apttus_Config2__OrderLineItem__c',
'Apttus_Config2__ProductId__c,Apttus_Config2__Quantity__c',
'Apttus_Config2__OrderId__c', '=',@OrderId1)

 Set @totalQuantity = 0.0


 set @rowCount1 = rowcount(@SalesforceObject1)
           if @rowCount1 > 0 then
           for @i = 1 to @rowCount1 do

 SET @CRMRow1 = Row(@SalesforceObject1,@i) 

 SET @Quantity1 = Field(@CRMRow1, 'Apttus_Config2__Quantity__c') 

 SET @Product1 = Field(@CRMRow1, 'Apttus_Config2__ProductId__c')
Set @ProductCode1 = Lookup('ent.Product2_Salesforce','ProductCode','Id',@Product1)

 Set @Translation1 = Lookup('ent.Apttus_Config2__ProductTranslation__c_Salesforce','Apttus_Config2__Name__c','Apttus_Config2__ProductCode__c',@ProductCode1,'Apttus_Config2__Language__c','nl_NL')



 ]%% 

    %%[  
   if @Translation1 != 'FREIGHT CHARGE' then

    Set @totalQuantity = ADD(@totalQuantity,@Quantity1)

   endif 
    next @i]
    %%[endif]%%
